css:
.foot {
    display: flex;
    align-self: flex-end;
    font-size: small;
}
.text{
    font-weight:bold;
}
.red-text{
    color: red;
    font-weight:bold;
    font-size: 10px;
}
.text-2{
    font-size: 10px;
}


input[placeholder] { 
  color: black !important;
  font-weight: bold !important;

}

/* table -1 */
 table, th, td {
            border: 1px solid black;
            border-collapse: collapse;
            width: 100%;
            color: black !important;
        }
        td{ 
            color: grey;
            padding: 10px;
            width: 50%;
        }
        .input{
            min-width: 100%;
            border: none;
            outline: none;

        }

.text{
    font-weight:bold;
}
.table{
    border: 1px solid black;
}
td{
    border-collapse: collapse;
    border-left: 1px solid #00000;
    border-bottom: 1px solid #00000;
}
.row td{
    border: 1px solid black;
}
.field-one-input{
    width:70%;
    position: relative;
    bottom: 10px;
}

.field-one-input .inp-one,.inp-two,.inp-three{
     border-top: none;
    margin: 0px;
    width: 100%;
    height: 25px;
    resize: none;
    outline: none;

}
.field-one-input .inp-one,.inp-two,.inp-three{
    outline:none;
}
.field-one-input p{
    padding-top:4px;
}
.field-one-input .inp-one textarea:focus{
    outline:none;
}
.field-one-input .inp-one textarea{
        margin: 0px;
    width: 100%;
    height: 25px;
    resize: none;
    outline: none;
    border-bottom: none;
    border-left: none;
    border-right: none;
}
.field-one-input .inp-two textarea{
        margin: 0px;
        border-left: none;
    width: 100%;
    height: 25px;
    resize: none;
    outline: none;
    border-bottom: none;
    border-right: none;
}
.field-one-input .inp-three textarea{
    margin: 0px;
    border-left: none;
    border-right: none;
    width: 100%;
    height: 25px;
    resize: none;
    outline: none;
    border-bottom: none;
}

.field-right-input{
    width: 30%;
}
.field-right-input .inp-four, .inp-five{
    border:1px solid black;
}
.field-right-input .inp-four{
    height: 50px;
    position: relative;
    bottom: 6px;
    border-right: none;
}
.field-right-input  .inp-five{
    position: relative;
    height: 51px;
    border-top: none;
    border-right: none;
    border-bottom: none;

}
.field-right-input label{
    padding:0px 5px;
}
.main-field-one{
    display:flex;
    justify-content: space-between;

}

.details{
    border: 1px solid;
    border-right: none;
    border-left: none;
    position: relative;
    bottom: 3px;
    padding: 2px;
}
.back textarea:focus{
    background:white;
}
.back textarea{
    background-color: eaf7f6;
}
.field-right-input .f-size{
    background-color: eaf7f6;
    font-size:20px;
    width: 100%;
    resize: none;
    border-right: none;
    border-top: none;
    height: 31px;
    outline:none;
    border-left: none;
}
.field-right-input .f-size:focus{
    background:white;
}
